#include "JanggiWidget.h"

#include <qevent.h>

JanggiWidget::JanggiWidget(QWidget* parent)
	: QWidget(parent)
{
	peiceImage[Blue][Gung].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Sa].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Cha].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Po].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Ma].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Sang].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Blue][Byeong].loadFromData(QByteArray::fromBase64(""));

	peiceImage[Red][Gung].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Sa].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Cha].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Po].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Ma].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Sang].loadFromData(QByteArray::fromBase64(""));
	peiceImage[Red][Byeong].loadFromData(QByteArray::fromBase64(""));


	turn = Red;
	gameend = false;
	gamestart = false;
	redtoggle = 0;
	bluetoggle = 0;

	setMouseTracking(true);
}


JanggiWidget::~JanggiWidget()
{}


void JanggiWidget::paint(QPainter* painter)
{
	for (int r = 0; r < janggi.GridRow; r++)
	{
		for (int c = 0; c < janggi.GridColumn; c++)
		{
			QRectF rect = convertRect({ r,c });
			painter->drawRect(rect);
			auto peice = janggi.getPeice({ r,c });
			if (peice)
				painter->drawImage(rect, peiceImage[peice->getTeam()][peice->getType()]);
		}
	}

	if (janggi.isValid(selection)) {
		QRectF rect = convertRect(selection);
		painter->setBrush(QBrush(QColor(255, 0, 0, 50)));
		painter->drawRect(rect);
	}

	if (janggi.isValid(focus)) {
		QRectF rect = convertRect(focus);
		painter->setBrush(QBrush(QColor(0, 0, 255, 50)));
		painter->drawRect(rect);
	}

	for (auto& pos : moves)
	{
		QRectF rect = convertRect(pos);
		painter->setBrush(QBrush(QColor(0, 255, 0, 50)));
		painter->drawRect(rect);
	}
	if (gameend) {
		QTextOption option(Qt::AlignCenter);
		QRectF rect = janggiRect();
		QFont font = painter->font();
		font.setPointSizeF(40);
		painter->setFont(font);
		painter->drawText(rect, "Game Over\n"+ (winner == Red ? QString::fromStdString("Red Win") : QString::fromStdString("Blue Win")), option);
	}
}

void JanggiWidget::start()
{
	gamestart = true;
}

void JanggiWidget::reset()
{
	janggi.reset();

	turn = Red;
	gameend = false;
	gamestart = false;
	redtoggle = 0;
	bluetoggle = 0;
	repaint();
}

void JanggiWidget::togglePosition(JanggiTeam team, int type)
{
	switch (team)
	{
	case Red:
		redtoggle ^= type;
		break;
	case Blue:
		bluetoggle ^= type;
		break;
	}
	janggi.reset(redtoggle, bluetoggle);
	repaint();
}

void JanggiWidget::paintEvent(QPaintEvent* event)
{
	QPainter painter;
	painter.begin(this);
	paint(&painter);
	painter.end();
}

void JanggiWidget::mouseMoveEvent(QMouseEvent* event)
{
	JanggiPosition pos = convertPosition(event->localPos());
	setFocus(pos);
}

void JanggiWidget::mousePressEvent(QMouseEvent* event)
{
	JanggiPosition pos = convertPosition(event->localPos());
	setSelection(pos);
}

JanggiPosition JanggiWidget::convertPosition(QPointF point)
{
	return { int(point.x() / (CellSize + CellPadding)),  int(point.y() / (CellSize + CellPadding)) };
}

QRectF JanggiWidget::convertRect(JanggiPosition pos)
{
	return{ pos.row * (CellSize + CellPadding) , pos.column * (CellSize + CellPadding) ,CellSize ,CellSize };
}

QRectF JanggiWidget::janggiRect()
{
	return{ 0,0,janggi.GridRow * (CellSize + CellPadding) , janggi.GridColumn * (CellSize + CellPadding) };
}

void JanggiWidget::setSelection(JanggiPosition pos)
{
	if (std::find(moves.begin(), moves.end(), pos) != moves.end()) {

		JanggiPeice* peice = janggi.getPeice(selection);
		if (janggi.getPeice(pos) && janggi.getPeice(pos)->getType() == Gung) {
			gameend = true;
			winner = peice->getTeam();
		}
		peice->move(pos);
		moves.clear();
		selection = { -1,-1 };
		if (turn == Red)
			turn = Blue;
		else
			turn = Red;
	}
	else if (selection != pos) {
		JanggiPeice* peice = janggi.getPeice(pos);
		if (gamestart && !gameend && peice && peice->getTeam() == turn) {
			moves = peice->getMoves();
			selection = pos;
		}
		else {
			moves.clear();
			selection = { -1,-1 };
		}
	}
	else {
		moves.clear();
		selection = { -1,-1 };
	}
	repaint();
}

void JanggiWidget::setFocus(JanggiPosition pos)
{
	if (focus != pos) {
		focus = pos;
		repaint();
	}
}
